<script>
    $(function(){
       $('.address').click(function(){
           $("#address_div div").removeClass('address_div1');
           $("#address_div div").addClass('address_div2');


           $(".class_div1").html("&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;");
           $(".class_div2").html("");


           $(this).parent().parent().addClass('address_div1');
          $(this).parent().prev().html('寄送至：');
          $(this).parent().next().html('修改本地址');

       });


       //倒计时
        var m="<?php echo date('i',($order['over_time']/1-time()))?>";
        var s="<?php echo date('s',($order['over_time']/1-time()/1))?>";
        s =s-1;
        if(s == -1) s = 0;
        setInterval(function(){
            if(s<10){
                $('#time').html(m+':0'+s);
            }else{
                $('#time').html(m+':'+s);
            }
            s--;
            if(s<0){
                s=59;
                m--;
            }
            if(m==0 && s==0){
                location.href="<?php echo Url::to(['home/order/over_time'])."&order=$order[order_sn]"?>"
            }
        },1000)
    });
</script>